INSERT INTO [tabCustom Script] VALUES ('Documentation-Client','2011-05-20 10:08:01','2011-05-20 10:08:01','Administrator','Administrator',0,NULL,NULL,NULL,NULL,'Documentation','Client','cur_frm.cscript["Print Document"] = function(doc,cdt,cdn){\n  $c("runserverobj",args={"method":"get_doc","docs":compress_doclist([doc])},\n    function(r,rt){\n      get_print_doc(r.message);\n    }\n  );\n}\n\nvar get_print_doc = function(html){\n//  alert("print - " + html)\n  var w = window.open("");\n  w.document.write(html);\n  w.document.close();\n}'),('Documentation-Server','2011-05-20 10:08:01','2011-05-20 10:08:01','Administrator','Administrator',0,NULL,NULL,NULL,NULL,'Documentation','Server','index_html = """\n  <div class = \"divmargin\" style=\"margin-left:%s;border\">\n    <table style=\"width:100%%;font-family: Arial, Sans; font-size:%s;font-weight:%s;\">\n      <tr>\n        <td style=\"width:40px;text-align:right;\">%s</td>\n        <td>%s</td>        \n      </tr>\n    </table>%s\n  </div>\n"""\n\nmod_html = """\n    <br><div class = \"divmargin\" style=\"margin-left:32px;\">\n      <table style=\"width:100%%;font-family: Arial, Sans; font-size: 20px;font-weight:normal;\">\n        <tr>\n          <td style=\"width:40px;text-align:right;\">%s</td>\n          <td>%s</td>\n        </tr>\n      </table>%s\n    </div>\n"""\n\nreport_html = """\n  <div class = \"divmargin\" style=\"margin-left:40px;\">\n    <table style=\"width:100%%;font-family: Arial, Sans; font-size: 18px;font-weight:normal;\">\n      <tr>\n        <td style=\"width:40px;text-align:right;\">%s</td>\n        <td>%s</td>\n      </tr>\n    </table>\n  </div>\n"""\n\ndoc_html="""\n    <br><div class = \"divmargin\" style=\"margin-left:40px;\">\n      <table style=\"width:100%%;font-family: Arial, Sans; font-size: 18px;font-weight:normal;\">\n        <tr>\n          <td style=\"width:40px;text-align:right;\">%s</td>\n          <td>%s</td>\n        </tr>\n      </table>%s\n    </div>\n"""\n\ndocsec_html="""\n  <div class = \"divmargin\" style=\"margin-left:48px;\">\n      <table style=\"width:100%%;font-family: Arial, Sans; font-size: 16px;font-weight:normal;\">\n        <tr>\n          <td style=\"width:40px;text-align:right;\">%s</td>\n          <td>%s</td>\n        </tr>\n      </table>%s\n  </div>\n  <div style=\"page-break-after:always;\">\n  </div>\n"""\n\nscreenshot_html="""\n  <br><div class = \"divmargin\"><img src=\"cgi-bin/getfile.py?name=%s\"></img></div>\n"""\n\ndocfields_html = """\n  <div class = \"divmargin\" style=\"margin-left:56px; width:800px;\">\n    <table style=\"border:%s;background-color:%s;\">\n      <tr>\n        <td>%s:</td>\n      </tr>\n      <tr>\n        <td><div class = \"divmargin\" style=\"margin-left:20px;width:550;\"><p><code>%s</code></p></div></td>  \n      </tr>\n    </table>\n  </div>\n"""\n\ndoctables_html = """\n  <div class = \"divmargin\" style=\"margin-left:56px;\">\n    <table style=\"width:100%%;\">\n      <tr>\n        <td>%s</td>\n      </tr>\n      <tr>\n        <td>%s</td>\n      </tr>      \n    </table>\n  </div>\n"""\n\nclass DocType:\n  def __init__(self,doc,doclist=[]):\n    self.doc = doc\n    self.doclist = doclist\n    self.main_html = ""\n    self.out = ""\n    self.index_html = ""\n    \n  def get_doc(self):\n    self.main_html = """<html><head><style type=\"text/css\">p{white-space:pre-wrap}</style><style>.simpletable {border-collapse:collapse;}</style><style>.simpletable td {padding: 3px; border: 1pt solid #000; font-family: Arial, Sans; font-size: 12px; vertical-align: top; }</style><style>.divmargin {margin-right:25px;}</style><style>.margin {margin:50px;}</style></head><body class=\"margin\" style=\"border-collapse:collapse\"><div style=\"margin:20px;border:1px solid #000;border-collapse:collapse;\">%s</div><div style=\"page-break-after:always;\"></div><div style=\"margin:20px;border:1px solid #000;border-collapse:collapse\">%s</div></body><html>"""\n\n#All modules\n    modules = sql(\"select module_name from [tabModule Def]\")\n    n=0\n    grp_mod_html= ""\n    grp_modindex_html = ""\n    temp_main_html = ""\n    for m in modules:\n      n1=0\n      n = n+1\n      rp_no = 0\n      grp_doc_html="""<br><div style=\"margin-left:40px;font-family: Arial, Sans; font-size: 16px;font-weight:normal;\">DocTypes</div>"""\n      grp_report_html="""<br><div style=\"margin-left:40px;font-family: Arial, Sans; font-size: 16px;font-weight:normal;\">Reports</div>"""\n      grp_docindex_html =""\n      reports = sql(\"select distinct criteria_name from [tabSearch Criteria] where module= "%s"\" % m[0])\n      doctypes = sql(\"select name from [tabDocType] where module ="%s"\" %(m[0]))\n\n      for rp in reports:\n        rp_no +=1\n        grp_report_html += report_html % (str(rp_no), rp[0])\n\n      for d in doctypes:\n        n1 = n1+1\n        n2 = 0\n        sr = ""\n        sr= str(n) + "." + str(n1)\n        grp_docsec_html = ""\n        grp_secindex_html = ""\n        grp_ss_html=""\n        docfields_name = sql(\"select name,autoname,description,client_script,server_code,client_string from [tabDocType] where name=%s\",d[0],as_dict = 1)\n#        docsections = ["Screenshot","Options","Permissions","Fields","Client Script", "Server Script","Client String"]\n        docsections = ["Options","Permissions","Fields"]        \n        for ds in docsections:\n          n2 = n2+1\n          sr1 = ""\n          sr1 = str(n)+"."+str(n1)+"."+str(n2)\n          for df in docfields_name:\n            grp_docfields_html = ""\n            if ds == "Options":\n              self.out = ""\n              if df["name"]:\n                grp_docfields_html += docfields_html % ("0pt none #FFFFFF","#FFFFFF","Name",str(df["name"]))\n              if df["autoname"]:             \n                grp_docfields_html += docfields_html % ("0pt none #FFFFFF","#FFFFFF","AutoName",str(df["autoname"]))\n              if df["description"]:\n                grp_docfields_html += docfields_html % ("0pt none #