from [tabDocField] where parent="%s" and fieldtype !="Section Break" and fieldtype != "Column Break" order by idx\" % d[0])\n\n              self.make_table(option1,d[0],lbl)\n\n              #table fields\n              tbl = sql(\"select options from [tabDocField] where parent="%s" and fieldtype="Table"\" % d[0])\n              for t in tbl:\n                option2 = sql(\"select label,fieldtype,fieldname,options,permlevel from [tabDocField] where parent="%s" and fieldtype !="Section Break" and fieldtype != "Column Break" order by idx\" % t[0])\n                self.make_table(option2,t[0],lbl)\n\n              grp_docfields_html +=doctables_html % ("", self.out)\n#              grp_docsec_html += docsec_html % (str(sr1),ds,grp_ss_html)\n          grp_docsec_html += docsec_html % (str(sr1), ds, grp_docfields_html)\n          grp_secindex_html += index_html % ("48px","16px","normal",str(sr1),ds,"")        \n        grp_doc_html += doc_html % (str(sr), d[0], grp_docsec_html)\n        grp_docindex_html += index_html % ("40px","18px","normal",str(sr), d[0],grp_secindex_html)      \n      grp_mod_html += mod_html % (str(n),m[0],grp_doc_html)\n      grp_mod_html += grp_report_html\n      grp_modindex_html += "<br>" + index_html % ("32px","20px","normal",str(n),m[0],grp_docindex_html)\n\n    self.main_html = self.main_html % (grp_modindex_html, grp_mod_html)\n    return self.main_html\n    \n  def make_table(self,opt,heading,lbl):\n    sr_no = 0\n    self.out += "<br><div style=\"margin-left:56px; width:100%%\">" + heading + "</div>"\n    self.out += """<br><div style=\"margin-left:56px; width:100%%\">\n                <table class=\"simpletable\" width=\"100%%\" style=\"page-break-after:always;\">\n                  <tr>\n                    <td style=\"width:5%%;\">Sr</td>"""\n\n    for l in lbl:\n      self.out += "<td>" + l + "</td>"\n    self.out +="</tr>"\n                  \n    for op in opt:\n      sr_no += 1\n      self.out = self.out + "<tr>"\n      self.out 