til.str_to_user(detail[0][2])\n                  + "<br><br>Contributors : " + contributors.join(", ");\n    set_field_options("History HTML", txt);\n  })*/\n  \n  var callback = function(r,rt){\n    detail = r.message.detail;\n    contributors = r.message.contributors;\n    \n    var txt = "<br>This is Revision - " + detail[0][0] + " of " + dn +"." + "<br>Last modified by " + detail[0][1] + " on " + dateutil.str_to_user(detail[0][2])\n                  + "<br><br>Contributors : " + contributors.join(", ");\n    set_field_options("History HTML", txt);\n  }\n  \n  args = {};\n  args.doc = doc; args.dt = dt; args.dn = dn;\n  $c_obj("Wiki Control","get_wiki_detail", docstring(args),callback);\n}\n\n// showing page history (keep this we will use this later)\n/*\npscript.show_page_history = function(){\n  if(!pscript.history_list){\n    history_list = new Listing("",1);\n    history_list.colwidths = ["30%","30%","40%"];\n    history_list.colnames = ["Revision", "Date", "Modified By"];\n    pscript.history_list = history_list;\n  }\n  \n  if(!pscript.history_div){ pscript.history_div = document.createElement("div"); }\n  pscript.history_div.innerHTML = "";\n\n  pscript.history_list.get_query = function(){\n    this.query = repl(\"select version_no, creation, modified_by, name from [tabWiki History] where reference="%(ref)s" order by version_no desc\", {"ref":pscript.cur_dn});\n    this.query_max = repl(\"select count(name) from [tabWiki History] where reference="%(ref)s"\", {"ref":pscript.cur_dn});\n  }\n  \n  pscript.history_list.show_cell = function(cell,ri,ci,d){\n    if(ci==0){\n      alert(d[ri][3]);\n      \n      var revision = $a(cell,"span","link_type");\n      revision.innerHTML = "Revision - " + d[ri][0];\n      revision.name = d[ri][3];\n      \n      revision.onclick = function(){ alert(1); alert(this.name); }\n    }\n    \n    if(ci==1){ cell.innerHTML = dateutil.str_to_user(d[ri][1]); }\n    \n    if(ci==2){ cell.innerHTML = d[ri][2]; }\n  }\n  \n  pscript.