e"    : res and res[0][1] or "",\n      "customer_address" : ret and ret[0][2] or ""}\n\n    return str(ret)\n\n\n  def get_cust_addr(self):\n    res = sql(\"select customer_name,address from [tabCustomer] where name = "%s"\"%self.doc.customer)\n    ret = { \n      "customer_name"    : res and res[0][0] or "",\n      "customer_address" : res and res[0][1] or ""}\n\n    return str(ret)\n\n\n    \n  def get_supp_values(self):\n    res = sql(\"select supplier,supplier_name,supplier_address from [tabPurchase Receipt] where name = "%s"\"%self.doc.purchase_receipt_no)\n    ret = {\n      "supplier" : res and res[0][0] or "",\n      "supplier_name" :res and res[0][1] or "",\n      "supplier_address" : res and res[0][2] or ""}\n    return str(ret)\n    \n\n  def get_supp_addr(self):\n    res = sql(\"select supplier_name,address from [tabSupplier] where name = "%s"\"%self.doc.supplier)\n    ret = {\n      "supplier_name" : res and res[0][0] or "",\n      "supplier_address" : res and res[0][1] or ""}\n    return str(ret)\n\n  # on update\n  def on_update(self):\n    obj = get_obj("Feed Control", "Feed Control")\n    \n    if not self.doc.creation:\n      obj.make_feed("created", self.doc.doctype, self.doc.name)\n    else:\n      obj.make_feed("modified", self.doc.doctype, self.doc.name)\n',NULL,NULL,' ','No',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'purpose','%(remarks)s',NULL),('Stock Entry Detail','2010-08-08 17:09:25','2010-12-14 15:39:25','Administrator','Administrator',0,NULL,NULL,NULL,NULL,NULL,NULL,1,23,'Material Management','MTND/.######','',NULL,'White:FFF',NULL,NULL,0,NULL,NULL,'',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Tray','','','','',NULL,NULL,' ','',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),('Stock Ledger','2010-08-08 17:09:25','2010-09-20 14:06:57','Administrator','Administrator',0,NULL,NULL,NULL,NULL,NULL,1,NULL,10,'Material Management',NULL,'',NULL,'White:FFF',1,1,0,NULL,NULL,'',NULL,NULL,NULL,NULL,1,NULL,NULL,NULL,NULL,'Simple','','','','class DocT