ledge user\n    msgprint(\"Stock Ledger Entries Updated Successfully.\")\n    \n    # update item valuation\n    if flt(self.doc.conversion_factor) != flt(1):\n      wh = sql(\"select name from [tabWarehouse]\")\n      for w in wh:\n        bin = sql(\"select name from [tabBin] where item_code = "%s" and warehouse = "%s"\" % (self.doc.item_code, w[0])) \n        if bin and bin[0][0]:\n          get_obj(\"Bin\", bin[0][0]).update_item_valuation(sle_id = "", posting_date = "", posting_time = "")\n\n    # acknowledge user\n    msgprint(\"Item Valuation Updated Successfully.\")\n\n  # Update Stock UOM              \n  def update_stock_uom(self):\n    # validate mandatory\n    self.validate_mandatory()\n    \n    # update item master\n    self.update_item_master()\n    \n    # update stock ledger entry\n    self.update_stock_ledger_entry()\n    \n    # update bin\n    self.update_bin()',NULL,NULL,' ','',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),('Supplier','2010-08-08 17:09:26','2011-05-10 17:42:21','Administrator','Administrator',0,NULL,NULL,NULL,NULL,'supplier_name,supplier_type',NULL,NULL,86,'SRM',NULL,NULL,NULL,'White:FFF',NULL,NULL,0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Tabbed',NULL,NULL,NULL,NULL,NULL,NULL,' ',NULL,NULL,NULL,NULL,NULL,1,NULL,'Master','supplier_type',' ',NULL),('Supplier Quotation','2010-10-27 15:24:21','2011-01-28 11:41:36','Administrator','Administrator',0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,241,'SRM','SQ.#####',NULL,NULL,'White:FFF',1,1,0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Tabbed',NULL,NULL,NULL,NULL,NULL,NULL,' ',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),('Supplier Quotation Detail','2010-12-14 17:52:48','2010-10-22 09:22:40','Administrator','Administrator',0,NULL,NULL,NULL,NULL,NULL,NULL,1,23,'Development',NULL,'',NULL,'White:FFF',NULL,NULL,0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Simple',NULL,NULL,NULL,NULL,NULL,NULL,' ','',NULL,NULL,NULL,NULL,NULL,NU