\n		me.dialog.hide();\n	}\n	var arg = {"usr":id, "role_list":role_list};\n	$c_obj("Test Control","update_roles",docstring(arg), callback);\n\n}\n\n// check if admin\nRoleObj.prototype.chk_if_admin = function(){\n	var me = this;\n	\n	if(me.roles_dict["Administrator"].checked){\n		var c = confirm(\"You have assigned Administrator role to this profile.\nThis profile will have acess to core functionality and will be able to change it.\n\nDo you want to continue anyway?\")\n		\n		return (c ? true : false);\n	}	\n}\n\n// photo upload\n// ================================================================================\n\n// photo dialog\nPhotoDialog = function(){\n	var d = new Dialog(null,200,"Upload Photo");\n	\n	d.make_body([\n		["HTML","wrapper"]\n	]);\n\n	this.body = $a(d.widgets["wrapper"],"div");\n	\n	this.dialog = d;\n}\n\n// upload object\nUploadObj = function(parent, form_name){\n	this.wrapper = $a(parent,"div");\n	\n	var dv = $a(parent,"div");	\n	dv.innerHTML = "<iframe id=\"ul_iframe\" name=\"ul_iframe\" src=\"blank1.html\" style=\"width:400px; height:300px; border:0px\"></iframe>";\n\n	var dv = $a(parent,"div");\n	dv.innerHTML = "<form id = \"upload_form\" method=\"POST\" enctype=\"multipart/form-data\" action=\""+outUrl+"\" target=\"ul_iframe\"></form>";\n	ul_form = dv.childNodes[0];\n			\n	var f_list = [];\n	\n	// file data\n	var message = $a(ul_form,"div","",{color:"#666", marginBottom:"5px"});\n	message.innerHTML = "Please select file to upload.";\n	\n	var fdata = $a_input($a(ul_form,"span"),"file",{name:"filedata"});\n\n	var cmd = $a_input($a(ul_form,"span"),"hidden",{name:"cmd"}); cmd.value = "upload_many";\n	var form = $a_input($a(ul_form,"span"),"hidden",{name:"form_name"}); form.value = form_name;\n	var btn = $a_input($a(ul_form,"div","",{width:"100px"}),"submit"); btn.value = "Upload";\n	btn.fdata = fdata; btn.message = message;\n\n	this.fdata = fdata;\n	this.message = message;\n	\n	btn.onclick = function(){\n		if(this.fdata.value != "")\n			this.message.innerHTML = "Uploading...";\n	}\n\n}\n\n// set profile pic\nset_profile_pic = function(file){\n	var dp = global_profile_object.dp;\n\n	set_user_img(dp,user,1)		 \n	\n	global_profile_object.upload_obj.fdata.value = "";\n	global_profile_object.upload_obj.message.innerHTML = "Please select file to upload."\n	global_profile_object.photo_dialog.dialog.hide();\n	\n	update_gateway_picture();\n}\n\n// update gateway picture\nupdate_gateway_picture = function(){\n	$c_obj("Test Control","update_gateway_picture","",function(r,rt){});\n}\n\n// Remove User Object\n// ---------------------------------------------------------------------------------------------\nRemoveUserObj = function(user){\n	var me = this;\n\n	var callback = function(r, rt){\n		if(r.message){		\n			var d = new Dialog(400,100,"Remove Users");\n		\n			d.make_body([["HTML", "remove_user_div"]]);\n			me.dialog = d;\n			var src = "https://"+r.message+"/index.py?usr="+user+"&acc="+locals["Control Panel"]["Control Panel"].account_id+"#Page/Remove User";\n			$a(d.widgets["remove_user_div"],"div").innerHTML = "<iframe src = \""+src+"\" width = \"100%\" height = \"100px\" style = \"border: 0px solid #ffffff\"></iframe>";\n			me.dialog.show();\n			me.dialog.onhide = function(){global_search_object.search();}\n		}\n		else{\n			msgprint(\"To use this feature for first time please logout and login again in your system\");\n			return;\n		}\n	}\n	$c_obj("Test Control","get_login_url","",callback);\n}\n\n// Switch user Obj\n// -------------------\nSwitchUserObj = function(){\n	var me = this;\n	var callback = function(r, rt){\n		if(r.message){		\n			var d = new Dialog(450,200,"Switch User");\n			d.make_body([["HTML", "switch_user_div"]]);\n			me.dialog = d;\n			var src = "https://"+r.message+"/index.py?acc="+locals["Control Panel"]["Control Panel"].account_id+"#Page/Switch User";\n			$a(d.widgets["switch_user_div"],"div").innerHTML = "<iframe src = \""+src+"\" width = \"100%\" height = \"150px\" style = \"border: 0px solid 